for(var coll=document.getElementById("flyers").getElementsByClassName("collapsible"),i=0;i<coll.length;i++)coll[i].addEventListener("click",function(){if(this.classList.contains("active"))this.classList.remove("active"),this.nextElementSibling.style.maxHeight=null;else{for(var k=0;k<coll.length;k++)coll[k].classList.remove("active");this.classList.toggle("active");for(var contents=document.getElementById("flyers").getElementsByClassName("content"),j=0;j<contents.length;j++)contents[j].style.maxHeight=null;var content=this.nextElementSibling;content.style.maxHeight=content.scrollHeight+"px"}});