function myFunction(){window.pageYOffset>sticky?header.classList.add("sticky"):header.classList.remove("sticky")}function slideshow(){setInterval(function(){nextSlide("dti-ss")},2e3)}function nextSlide(e){var t=document.getElementById(e).getElementsByClassName("slide");t.index=t.index||0,t[t.index].className="slide",t.index=(t.index+1)%t.length,t[t.index].className="slide showing"}function collapsible(e,t,s){const n=document.getElementById(e).getElementsByClassName(t),i=document.getElementById(e).getElementsByClassName(s);for(let e=0;e<n.length;e++)n[e].addEventListener("click",function(){for(let e=0;e<i.length;e++)i[e].style.height="0px";if(this.classList.contains("active"))this.classList.remove("active");else{for(let e=0;e<n.length;e++)n[e].classList.remove("active");this.classList.toggle("active");var e=this.nextElementSibling;e.style.height=e.scrollHeight+"px"}})}window.onscroll=function(){myFunction()};var header=document.getElementById("myHeader"),sticky=header.offsetTop;onload=slideshow,collapsible("headlines","collapsible","content");