function myFunction(){window.pageYOffset>sticky?header.classList.add("sticky"):header.classList.remove("sticky")}function slideshow(){setInterval(function(){nextSlide("mi-ss")},2e3)}function nextSlide(e){var n=document.getElementById(e).getElementsByClassName("slide");n.index=n.index||0,n[n.index].className="slide",n.index=(n.index+1)%n.length,n[n.index].className="slide showing"}window.onscroll=function(){myFunction()};var header=document.getElementById("myHeader"),sticky=header.offsetTop;onload=slideshow;