for(var collapsible_2=document.getElementById("collapsible").getElementsByClassName("collapsible"),i=0;i<collapsible_2.length;i++)collapsible_2[i].addEventListener("click",function(){for(var contents=document.getElementById("collapsible").getElementsByClassName("content"),j=0;j<contents.length;j++)contents[j].style.maxHeight=null;if(this.classList.contains("active"))this.classList.remove("active");else{for(var k=0;k<collapsible_2.length;k++)collapsible_2[k].classList.remove("active");this.classList.toggle("active");var content=this.nextElementSibling;content.style.maxHeight=content.scrollHeight+"px"}});